class AcronymManager: ObservableObject {
    @Published var acronyms: [Acronym] = []
    @Published var triActuel: TriAcronyme = .aucun
    
    init() {
        loadDefaultAcronyms()
    }
    
    func trierAcronymes(par tri: TriAcronyme) {
        triActuel = tri
        switch tri {
        case .alphabetique:
            acronyms.sort { $0.letters < $1.letters }
        case .categorie:
            acronyms.sort { $0.category.rawValue < $1.category.rawValue }
        case .aucun:
            loadDefaultAcronyms()
        }
    }

