    func addAcronym(_ acronym: Acronym) {
        acronyms.append(acronym)
    }
    
    func deleteAcronym(_ acronym: Acronym) {
        acronyms.removeAll { $0.id == acronym.id }
    }
    
    func searchAcronyms(with query: String) -> [Acronym] {
        guard !query.isEmpty else { return acronyms }
        return acronyms.filter {
            $0.letters.localizedCaseInsensitiveContains(query) ||
            $0.fullForm.localizedCaseInsensitiveContains(query)
        }
    }
